// Inicializamos el juego
function initialize() {

    // Crea las casillas del juego
    for (let r = 0; r < altura; r++) {
        for (let c = 0; c < anchura; c++) {
            // <span id="0-0" class="baldosa">P</span>
            let baldosa = document.createElement("span");
            baldosa.id = r.toString() + "-" + c.toString();
            baldosa.classList.add("baldosa");
            baldosa.innerText = "";
            document.getElementById("board").appendChild(baldosa);
        }
    }

    // Creando teclado
    let teclado = [
        ["Q", "W", "E", "R", "T", "Y", "U", "I", "O", "P"],
        ["A", "S", "D", "F", "G", "H", "J", "K", "L", "Ñ"],
        ["Enter", "Z", "X", "C", "V", "B", "N", "M", "⌫" ]
    ]

    // La idea es generar un teclado en la pagina utilizando elementos <div> y aplicando estilos CSS a las filas y teclas del teclado. Cada tecla se puede hacer clic y se asigna un id único, lo que permite realizar acciones según la tecla presionada.

    // Recorro cada fila del teclado y creo un div para cada fila
    for (let i = 0; i < teclado.length; i++) {
        let filaActual = teclado[i];
        let tecladoFila = document.createElement("div");
        tecladoFila.classList.add("teclado-fila");

        // Recorro cada tecla de la fila y creo un div para cada tecla
        for (let j = 0; j < filaActual.length; j++) {
            let keyBaldosa = document.createElement("div");

            let key = filaActual[j];
            keyBaldosa.innerText = key;
            if (key == "Enter") {
                keyBaldosa.id = "Enter";
            }
            else if (key == "⌫") {
                keyBaldosa.id = "Backspace";
            }
            else if(key == "Ñ") {
                keyBaldosa.id = "KeyÑ";
            }
            else if ("A" <= key && key <= "Z") {
                keyBaldosa.id = "Key" + key; // "Key" + "A";
            }

            // Añado un evento a cada tecla cuando se pulse, se llama a la función processKey
            keyBaldosa.addEventListener("click", processKey);

            if (key == "Enter") {
                keyBaldosa.classList.add("enter-key-baldosa");
            } else {
                keyBaldosa.classList.add("key-baldosa");
            }
            // Se coloca dentro de la fila actual del teclado
            tecladoFila.appendChild(keyBaldosa);
        }
        // Se coloca dentro del cuerpo del documento HTML
        document.body.appendChild(tecladoFila);
    }

    // Evento cuando el usuario suelta una tecla del teclado, se ejecutará la función processInput
    document.addEventListener("keyup", (e) => {
        processInput(e);
    });
}

// Se encarga de crear un objeto con la propiedad "code" que contiene el ID de la tecla pulsada y luego llama a processInput() para procesar esa entrada.
function processKey() {
    e = { "code" : this.id };
    processInput(e);
}

// Controla la entrada de las teclas.
function processInput(e) {
    if (gameOver) return;

    // Permite ingresar letras en las baldosas correspondientes
    // Para acceder a cada letra se utiliza el "e.code" ya que la propiedad "code" contiene el ID de la tecla pulsada
    if ("KeyA" <= e.code && e.code <= "KeyZ" || e.code == "KeyÑ") {
        if (col < anchura) {
            let baldosaActual = document.getElementById(fila.toString() + '-' + col.toString());
            if (baldosaActual.innerText == "") {
                baldosaActual.innerText = e.code[3]; // e.code[3] se utiliza para acceder a la letra de la tecla pulsada. Por ejemplo, si se pulsa la tecla "A", e.code[3] será "A"
                col += 1;
            }
        }
    }
    // Permite retroceder para borrar letras
    else if (e.code == "Backspace") {
        if (0 < col && col <= anchura) {
            col -=1;
        }
        let baldosaActual = document.getElementById(fila.toString() + '-' + col.toString());
        baldosaActual.innerText = "";
    }
    // Permite pasar a la siguiente fila, para ello tendremos que ver si la palabra es correcta o no
    else if (e.code == "Enter") {
        actualizar();
    }
    // Verifica si se ha alcanzado el límite de filas y muestra la palabra si es el caso, en este caso has perdido
    if (!gameOver && fila == altura) {
        gameOver = true;
        setTimeout(function() {
            alert(document.getElementById("answer").innerText = "La palabra era: " + word);
        }, 500);
    }
}


function actualizar() {
    let guess = "";
    document.getElementById("answer").innerText = "";

    // Recorre cada baldosa en la fila actual del teclado y crea una cadena de texto con las letras de las baldosas
    for (let c = 0; c < anchura; c++) {
        let baldosaActual = document.getElementById(fila.toString() + '-' + c.toString());
        let letra = baldosaActual.innerText;
        guess += letra;
    }

    guess = guess.toLowerCase(); //case sensitive para que no distinga entre mayúsculas y minúsculas
    console.log(guess);

    // Verifica si la palabra está en la lista de palabras
    if (!wordList.includes(guess)) {
        setTimeout(function() {
            alert(document.getElementById("answer").innerText = "No está en la lista");
        }, 500);
        return;
    }

    // Empieza proceso de adivinar
    let correcto = 0; //número de letras correctas

    let letrasCount = {}; //realiza un seguimiento de la frecuencia de letra, por ejemplo: ABAJO -> {A: 2, B: 1, J: 1, O: 1}
    for (let i = 0; i < word.length; i++) {
        let letra = word[i];

        // Si la letra ya está en el objeto, aumenta el conteo
        if (letrasCount[letra]) {
            letrasCount[letra] += 1;
        }
        else {
            letrasCount[letra] = 1;
        }
    }

    console.log(letrasCount);

    //Primera iteración, comprueba todas las correctas primero
    for (let c = 0; c < anchura; c++) {
        let baldosaActual = document.getElementById(fila.toString() + '-' + c.toString());
        let letra = baldosaActual.innerText;

        // Verifica si están en la posición correcta
        if (word[c] == letra) {
            baldosaActual.classList.add("correcto");

            let keyBaldosa = document.getElementById("Key" + letra);
            keyBaldosa.classList.remove("presente");
            keyBaldosa.classList.add("correcto");

            correcto += 1;
            letrasCount[letra] -= 1; //reduce el conteo de letras
        }

        // Verifica si las 5 letras son correctas
        if (correcto == anchura) {
            gameOver = true;
            setTimeout(function() {
                if (fila == 1) {
                    alert("¡Enhorabuena! Has acertado la palabra en " + fila + " intento");
                } else {
                    alert("¡Enhorabuena! Has acertado la palabra en " + fila + " intentos");
                }
            }, 1000);
        }

        // Verifica si la letra está en la palabra pero no en la posición correcta, ausente o presente
        // saltar la letra si se ha marcado como correcta
        if (!baldosaActual.classList.contains("correcto")) {
            //nos aseguramos que no cuenta doble
            if (word.includes(letra) && letrasCount[letra] > 0) {
                baldosaActual.classList.add("presente");

                let keyBaldosa = document.getElementById("Key" + letra);
                if (!keyBaldosa.classList.contains("correcto")) {
                    keyBaldosa.classList.add("presente");
                }
                letrasCount[letra] -= 1;
            } // No está en la palabra o estaba en la palabra pero las letras se agotaron para evitar el conteo excesivo
            else {
                baldosaActual.classList.add("ausente");
                let keyBaldosa = document.getElementById("Key" + letra);
                keyBaldosa.classList.add("ausente")
            }
        }
    }

    console.log(letrasCount);

    fila += 1; //empieza nueva fila
    col = 0; //empieza en 0 por la nueva fila
}

